mapper.select:
SELECT count(*)
FROM score
WHERE classNO = #{classNO} and memberNO = #{memberNO}

SELECT classPersonnel
FROM class
WHERE classNO = #{classNO}

SELECT count(*)
FROM score
WHERE classNO = #{classNO}

SELECT classCredit
FROM class
WHERE classNO = #{classNO}

mapper.delete:
delete
from score
where memberNO = #{memberNO} AND classNO = #{classNO}
